  sub custom_metric_calc {
      my ($self, %options) = @_;
  
      $self->{result_values}->{value} = $options{new_datas}->{$self->{instance} . '_value'};
      $self->{result_values}->{unit} = $options{new_datas}->{$self->{instance} . '_unit'};
